var list = require('../../app/urls.json');
var Xray = require("x-ray");

var xray = new Xray();
var i;
for(i = 0; i < list.length; i++) {
    var file = '../../app/results' + list[i].id + '.json';

    xray(list[i].url, {
        Data: ['td.ZAHL']

    })(
        function(err, obj) {
            obj.name = 'huhu';
            console.log(obj);
        }).write(file);
}



